import { Link , useNavigate } from "react-router-dom";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import React, { useState } from "react";
import {handleError , handleSuccess} from "../utils";
import {ToastContainer} from "react-toastify";

export default function Signup() {
  const navigate = useNavigate();
  const [signupInfo , setSignupInfo] = useState({
    name:"",
    email:"",
    password:""
  });

  const handleChange=(e)=>{
    const {name , value} = e.target;
    console.log(name , value);
    const copyDetails = {...signupInfo};
    copyDetails[name] =  value;
    setSignupInfo(copyDetails);
  }

  const handleSignup=async(e)=>{
    e.preventDefault();
    const {name , email , password} = signupInfo;

    if(!name || !email || !password){
      return handleError("Please enter the values")
    }
    try{
      const uri = "http://localhost:8080/signup";
      const responce = await fetch(uri , {
        method:"POST",
        headers:{
          "content-type" :  "application/json",
        },
        body:JSON.stringnify(signupInfo),
      })

      const result = await responce.json();
      const {message , success , error} = result;
      if(success){
        handleSuccess("Registered Successfully");
        setTimeout(()=>{
          navigate("/login");
        } , 1000);
      }
      else if(error){
        const details =  error?.details[0].message;
        handleError(details);
      }
      else if(!success){
        handleError("Failed!!")
      }
    }
    catch(err){
      handleError("Error");
    }
  }


  return (
    <div className="container mt-5">
      <div className="row mt-5">
        <div className="col d-flex justify-content-center mt-5 p-3 bold">
          <Box
            sx={{
              width: 400,
              display: "flex",
              flexDirection: "column",
              gap: 2, // ye har field ke beech equal space dega
            }}
          >
            <h3 className="text-center mb-3">Create Your LifeConnect Account</h3>
              <form onSubmit={handleSignup}>
            <TextField fullWidth label="Full Name" id="fullName" name="name" onChange={handleChange}/>
            <TextField fullWidth label="Email" id="email"  name="email" onChange={handleChange}/>
            <TextField fullWidth label="Password" id="password" type="password"  name="password"  onChange={handleChange}/>

            <Button type="Submit"
              variant="contained"
              sx={{
                backgroundColor: "#cf2b2b",
                "&:hover": { backgroundColor: "#b12222" },
              }}
            >
              Sign Up
            </Button>

            <p className="text-center mt-2">
              Already have an account?{" "}
              <Link to="/login" style={{ color: "#cf2b2b", textDecoration: "none" }}>
                Login
              </Link>
            </p>
            </form>
            <ToastContainer/>
          </Box>
        </div>
      </div>
    </div>
  );
}
