import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Cards.css"; // new CSS file for parallax + card styles

export default function Cards() {
  const [donors, setDonors] = useState([]);
  const [recipients, setRecipients] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/cards/donar")
      .then((res) => setDonors(res.data.donors || []))
      .catch((err) => console.log("Error fetching donors", err));

    axios.get("http://localhost:8080/cards/recipient")
      .then((res) => setRecipients(res.data.recipient || []))
      .catch((err) => console.log("Error fetching recipient", err));
  }, []);

  // Merge lists alternately
  const mergedCards = [];
  const maxLen = Math.max(donors.length, recipients.length);
  for (let i = 0; i < maxLen; i++) {
    if (donors[i]) mergedCards.push({ ...donors[i], type: "donor" });
    if (recipients[i]) mergedCards.push({ ...recipients[i], type: "recipient" });
  }

  return (
    <div className="parallax-wrapper">
      {/* optional dim overlay to improve contrast */}
      <div className="parallax-overlay">
        <div className="container p-4">
          <h2 className="cards-heading text-center mb-4">Available Donors & Recipients</h2>

          <div className="row g-4 d-flex justify-content-center">
            {mergedCards.map((user, index) => (
              <div key={index} className="col-sm-10 col-md-6 col-lg-4 d-flex justify-content-center">
                <div className={`card glass-card ${user.type === "donor" ? "donor" : "recipient"}`}>
                  <div className="card-media">
                    <img src="/logo.png" alt="profile" />
                  </div>

                  <div className="card-body text-center">
                    <h4 className="fw-bold mb-2">{user.name}</h4>

                    <p className="m-0 fs-6">
                      <strong>Age:</strong> {user.age} &nbsp; | &nbsp;
                      <strong>City:</strong> {user.city}
                    </p>

                    <p className="mt-2 fs-6">
                      {user.type === "donor"
                        ? `Blood Group: ${user.blood_group}`
                        : `Required Blood Group: ${user.blood_group}`}
                    </p>

                    <button className="contact-btn">Contact me</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
