import {toast} from "react-toastify";

export const handleSuccess=(msg)=>{
    toast.success(msg,{
        position:"top-right",
        autoClose:"3000",
    });
}

export const handleError=(msg)=>{
    toast.success(msg,{
        position:"top-right",
        autoClose:"3000",
    });
}
