import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import "./Donar.css";   // same CSS for background

export default function Recipient() {
  return (
    <div className="parallax-bg">

      <Paper elevation={6} className="p-4" 
        style={{ width: "450px", borderRadius: "16px", background: "rgba(255,255,255,0.85)" }}
      >
        
        <Typography variant="h5" align="center" gutterBottom style={{ fontWeight: "bold" }}>
          Become a Recipient
        </Typography>

        <Typography variant="body2" align="center" color="text.secondary" className="mb-3">
          Fill the details below to request required blood
        </Typography>
 <form>
        <Box
          component="form"
          sx={{ "& > :not(style)": { m: 1, width: "37ch" } }}
          noValidate
          autoComplete="off"
          className="d-flex flex-column align-items-center"
        >
       
          <TextField variant="standard" label="Full Name" />
          <TextField variant="standard" label="Email" />
          <TextField variant="standard" label="Password" type="password" />
          <TextField variant="standard" label="Required Blood Group" />
          <TextField variant="standard" label="Age" type="number" />
          <TextField variant="standard" label="City" />
        </Box>

        <Stack direction="row" spacing={2} className="mt-4 justify-content-center">
          <Button 
            variant="contained" 
            size="large" 
            style={{ padding: "10px 30px", borderRadius: "8px" }}
          >
            Request Blood
          </Button>
        </Stack>
</form>
      </Paper>
    </div>
  );
}
