import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Cards({ search }) {
  const [donors, setDonors] = useState([]);
  const [recipients, setRecipients] = useState([]);
  const [merged, setMerged] = useState([]);

  // Fetch donors + recipients
  useEffect(() => {
    axios.get("http://localhost:8080/cards/donar")
      .then((res) => setDonors(res.data))
      .catch((err) => console.log(err));

    axios.get("http://localhost:8080/cards/recipient")
      .then((res) => setRecipients(res.data))
      .catch((err) => console.log(err));
  }, []);

  // Alternate Merge: Donor → Recipient → Donor → Recipient
  useEffect(() => {
    const temp = [];
    const maxLen = Math.max(donors.length, recipients.length);

    for (let i = 0; i < maxLen; i++) {
      if (donors[i]) temp.push({ type: "donor", data: donors[i] });
      if (recipients[i]) temp.push({ type: "recipient", data: recipients[i] });
    }

    setMerged(temp);
  }, [donors, recipients]);

  // Search filter
  const filteredData = merged.filter((item) =>
    item.data.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: "20px" }}>
      {filteredData.map((item, index) => (
        <div
          key={index}
          style={{
            backgroundColor: item.type === "donor" ? "#D34E4E" : "#8BAE66",
            color: "white",
            padding: "20px",
            borderRadius: "14px",
            marginBottom: "15px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
          }}
        >
          <h2 style={{ marginBottom: "10px" }}>
            {item.type === "donor" ? "Donor" : "Recipient"}
          </h2>

          <p><strong>Name:</strong> {item.data.name}</p>
          <p><strong>City:</strong> {item.data.city}</p>
          <p><strong>Age:</strong> {item.data.age}</p>
          <p><strong>Blood Group:</strong> {item.data.blood_group}</p>
        </div>
      ))}
    </div>
  );
}
