import React from "react";

export default function Hero() {
  return (
    <div className="container mt-5 mb-5">
      <h1 className="text-center mt-5 fs-3 mb-5 p-5 " style={{color:"white"}}>
        “𝐋𝐢𝐟𝐞𝐂𝐨𝐧𝐧𝐞𝐜𝐭’𝐬 𝐦𝐢𝐬𝐬𝐢𝐨𝐧 𝐢𝐬 𝐭𝐨 𝐛𝐮𝐢𝐥𝐝 𝐚 𝐜𝐨𝐦𝐩𝐚𝐬𝐬𝐢𝐨𝐧𝐚𝐭𝐞 𝐜𝐨𝐦𝐦𝐮𝐧𝐢𝐭𝐲 𝐰𝐡𝐞𝐫𝐞 𝐧𝐨 𝐨𝐧𝐞 𝐬𝐭𝐫𝐮𝐠𝐠𝐥𝐞𝐬 𝐚𝐥𝐨𝐧𝐞 𝐢𝐧 𝐭𝐢𝐦𝐞𝐬 𝐨𝐟 𝐦𝐞𝐝𝐢𝐜𝐚𝐥 𝐧𝐞𝐞𝐝. 𝐓𝐡𝐫𝐨𝐮𝐠𝐡 𝐚 𝐯𝐞𝐫𝐢𝐟𝐢𝐞𝐝, 𝐫𝐞𝐚𝐥-𝐭𝐢𝐦𝐞 𝐬𝐲𝐬𝐭𝐞𝐦, 𝐰𝐞 𝐜𝐨𝐧𝐧𝐞𝐜𝐭 𝐝𝐨𝐧𝐨𝐫𝐬 𝐚𝐧𝐝 𝐫𝐞𝐜𝐢𝐩𝐢𝐞𝐧𝐭𝐬 𝐬𝐞𝐚𝐦𝐥𝐞𝐬𝐬𝐥𝐲 — 𝐞𝐧𝐬𝐮𝐫𝐢𝐧𝐠 𝐭𝐡𝐚𝐭 𝐡𝐞𝐥𝐩 𝐢𝐬 𝐚𝐥𝐰𝐚𝐲𝐬 𝐣𝐮𝐬𝐭 𝐨𝐧𝐞 𝐜𝐥𝐢𝐜𝐤 𝐚𝐰𝐚𝐲.”🌈
      </h1>
    </div>
  );
}
