import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Cards() {
  const [donors, setDonors] = useState([]);
  const [recipients, setRecipients] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/cards/donar")
      .then((res) => setDonors(res.data.donors || []))
      .catch((err) => console.log("Error fetching donors", err));

    axios.get("http://localhost:8080/cards/recipient")
      .then((res) => setRecipients(res.data.recipient || []))
      .catch((err) => console.log("Error fetching recipient", err));
  }, []);

  //  Alternate Merge of Donor + Recipient
  const mergedCards = [];
  const maxLen = Math.max(donors.length, recipients.length);

  for (let i = 0; i < maxLen; i++) {
    if (donors[i]) mergedCards.push({ ...donors[i], type: "donor" });
    if (recipients[i]) mergedCards.push({ ...recipients[i], type: "recipient" });
  }

  return (
    <div className="container p-5">
      <div className="row g-4 p-3">

        {mergedCards.map((user, index) => (
          <div key={index} className="col-md-4 d-flex justify-content-center">

            <div
              className="card text-dark shadow-lg"
              style={{
                width: "20rem",
                borderRadius: "20px",
                overflow: "hidden",
                backgroundColor: user.type === "donor" ? "#D34E4E" : "#8BAE66",
                transition: "transform 0.3s ease, box-shadow 0.3s ease",
                cursor: "pointer",
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = "scale(1.05)";
                e.currentTarget.style.boxShadow = "0px 10px 20px rgba(0,0,0,0.3)";
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = "scale(1)";
                e.currentTarget.style.boxShadow = "0px 4px 10px rgba(0,0,0,0.2)";
              }}
            >
              <img
                src="/logo.png"
                className="card-img-top border-bottom"
                alt="profile"
                style={{
                  height: "180px",
                  objectFit: "cover",
                  borderBottom: "3px solid black",
                }}
              />

              <div className="card-body">
                <h4 className="fw-bold mb-3" style={{ color: "black" }}>
                  {user.name}
                </h4>

                <div className="d-flex justify-content-between mb-2">
                  <p className="m-0 fw-semibold" style={{ color: "black" }}>
                    Age: {user.age}
                  </p>
                  <p className="m-0 fw-semibold" style={{ color: "black" }}>
                    City: {user.city}
                  </p>
                </div>

                <p className="fw-semibold mb-3" style={{ color: "black" }}>
                  {user.type === "donor"
                    ? `Blood Group: ${user.blood_group}`
                    : `Required Blood Group: ${user.blood_group}`}
                </p>

                <button
                  className="fw-bold"
                  style={{
                    background: "white",
                    border: "none",
                    padding: "10px 20px",
                    borderRadius: "30px",
                    color: "#333",
                    fontSize: "15px",
                  }}
                >
                  Contact Me
                </button>
              </div>
            </div>

          </div>
        ))}

      </div>
    </div>
  );
}
