import React from "react";

export default function Hero() {
  return (
    <div 
      className="container mt-5 mb-5 p-5 text-center animate-fade"
      style={{
        background: "rgba(255, 255, 255, 0.7)",
        backdropFilter: "blur(6px)",
        borderRadius: "20px",
        boxShadow: "0 8px 25px rgba(0,0,0,0.1)",
      }}
    >
      <h1 className="fs-3 text-dark lh-base" style={{ fontWeight: 500 }}>
        “𝐋𝐢𝐟𝐞𝐂𝐨𝐧𝐧𝐞𝐜𝐭’𝐬 𝐦𝐢𝐬𝐬𝐢𝐨𝐧 𝐢𝐬 𝐭𝐨 𝐛𝐮𝐢𝐥𝐝 𝐚 𝐜𝐨𝐦𝐩𝐚𝐬𝐬𝐢𝐨𝐧𝐚𝐭𝐞 𝐜𝐨𝐦𝐦𝐮𝐧𝐢𝐭𝐲
        𝐰𝐡𝐞𝐫𝐞 𝐧𝐨 𝐨𝐧𝐞 𝐬𝐭𝐫𝐮𝐠𝐠𝐥𝐞𝐬 𝐚𝐥𝐨𝐧𝐞 𝐢𝐧 𝐭𝐢𝐦𝐞𝐬 𝐨𝐟 𝐦𝐞𝐝𝐢𝐜𝐚𝐥 𝐧𝐞𝐞𝐝. 𝐖𝐞
        𝐜𝐨𝐧𝐧𝐞𝐜𝐭 𝐝𝐨𝐧𝐨𝐫𝐬 𝐚𝐧𝐝 𝐫𝐞𝐜𝐢𝐩𝐢𝐞𝐧𝐭𝐬 𝐫𝐞𝐚𝐥-𝐭𝐢𝐦𝐞 — 𝐡𝐞𝐥𝐩 𝐢𝐬 𝐣𝐮𝐬𝐭 𝐨𝐧𝐞
        𝐜𝐥𝐢𝐜𝐤 𝐚𝐰𝐚𝐲.” 🌈
      </h1>
    </div>
  );
}
