import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Cards.css";

export default function Cards({ search }) {
  const [donors, setDonors] = useState([]);
  const [recipients, setRecipients] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/cards/donar")
      .then(res => setDonors(res.data.donors || []))
      .catch(err => console.log("Error fetching donors", err));

    axios.get("http://localhost:8080/cards/recipient")
      .then(res => setRecipients(res.data.recipient || []))
      .catch(err => console.log("Error fetching recipient", err));
  }, []);

  const mergedCards = [
    ...donors.map(d => ({ ...d, type: "donor" })),
    ...recipients.map(r => ({ ...r, type: "recipient" }))
  ];

  const filteredCards = mergedCards.filter(u => {
    const text = search.toLowerCase();
    return (
      u.name?.toLowerCase().includes(text) ||
      u.city?.toLowerCase().includes(text) ||
      u.blood_group?.toLowerCase().includes(text) ||
      u.type?.toLowerCase().includes(text)
    );
  });

  return (
    <div className="parallax-bg">
      <div className="container card-container">
        <h2 className="cards-heading text-center mb-5">Available Donors & Recipients</h2>

        <div className="row g-4 justify-content-center">
          {filteredCards.length === 0 && (
            <h4 className="text-center text-white">No results found</h4>
          )}

          {filteredCards.map((user, index) => (
            <div key={index} className="col-sm-10 col-md-6 col-lg-4 d-flex justify-content-center">
              <div className={`card card-custom shadow-lg ${user.type === "donor" ? "donor-card" : "recipient-card"}`}>

                <span className={`badge-tag ${user.type === "donor" ? "donor-badge" : "recipient-badge"}`}>
                  {user.type === "donor" ? "Donor" : "Recipient"}
                </span>

                <img src="/logo.png" alt="profile" className="card-img-top" />

                <div className="card-body text-center">
                  <h4 className="fw-bold mb-2">{user.name}</h4>

                  <p className="m-0 fs-6">
                    <strong>Age:</strong> {user.age} | <strong>City:</strong> {user.city}
                  </p>

                  <p className="mt-2 fs-6">
                    {user.type === "donor"
                      ? `Blood Group: ${user.blood_group}`
                      : `Required: ${user.blood_group}`}
                  </p>

                  <button className="contact-btn mt-2">Contact me</button>
                </div>

              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
