import React , {useState} from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import "./Donar.css";
import {useNavigate} from "react-router-dom";
import { ToastContainer } from "react-toastify";
import {handleSuccess , handleError} from "../utils";

export default function Donar() {
  const navigate = useNavigate();

  const [donarInfo , setDonarInfo] = useState({
    name:"",
    email:"",
    password:"",
    blood_group:"",
    city:"",
  });

  function handleChange(e){
    const {name , value} = e.target;
    setDonarInfo({...donarInfo , [name]:value});
  }

  return (
    <div className="parallax-bg">
         <form> 
      <Paper elevation={6} className="p-4" style={{ width: "450px", borderRadius: "16px", background: "rgba(255,255,255,0.85)" }}>
        
        <Typography variant="h5" align="center" gutterBottom style={{ fontWeight: "bold" }}>
          Become a Donor
        </Typography>

        <Typography variant="body2" align="center" color="text.secondary" className="mb-3">
          Fill the details below to register as a donor
        </Typography>

        <Box
          component="form"
          sx={{ "& > :not(style)": { m: 1, width: "37ch" } }}
          noValidate
          autoComplete="off"
          className="d-flex flex-column align-items-center"
        >
       
          <TextField variant="standard" label="Full Name" onChange={handleChange} value={donarInfo.name}/>
          <TextField variant="standard" label="Email" onChange={handleChange} value={donarInfo.email}/>
          <TextField variant="standard" label="Password" type="password" onChange={handleChange} value={donarInfo.password}/>
          <TextField variant="standard" label="Blood Group" onChange={handleChange} value={donarInfo.blood}/>
          <TextField variant="standard" label="City" onChange={handleChange}/>
        </Box>

        <Stack direction="row" spacing={2} className="mt-4 justify-content-center">
          <Button variant="contained" size="large" style={{ padding: "10px 30px", borderRadius: "8px" }}>
            Become Donor
          </Button>
        </Stack>

      </Paper>
      </form>
      <ToastContainer/>
      
    </div>
  );
}
