import { Link  , useNavigate} from "react-router-dom";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import  React , {useState} from "react";
import {ToastContainer} from "react-toastify";
import {handleError , handleSuccess} from "../utils"

export default function Login() {
  const naviagte = useNavigate();
  const [loginInfo , setLoginInfo] = useState({
    email:"",
    password:"",
  });

  const handleChange=(e)=>{
    const {name , value} = e.target;
    const copyDetails = {...loginInfo};
    copyDetails[name] = value;
    setLoginInfo(copyDetails);
  }

  const handleLogin=async(e)=>{
    e.preventDefault();
    const {email , password} = loginInfo;
    if(!email || !password){
      return handleError("Please enter the details");
    }
    try{
        const uri = "http://localhost:8080/login";
        const responce = await fetch(uri ,{
          method:"POST",
          headers:{
            "content-type":"application/json"
          },
          body:JSON.stringify(loginInfo),
        })

        const result = await responce.json();
        const {message , error , success , jwtToken , name} = result;
        if(success){
          handleSuccess(`&{user.name} , login successfully`);
          localStorage.setItem("Token" , jwtToken);
          localStorage.setItem("loggedInuser" , name);
          setTimeout(()=>{
            naviagte("/home");
          },1000);
        }
        else if(error){
          const details = error?.details[0].message;
          handleError("Sommething went wrong!!")
        }
        else if(!success){
            handleError("Something went wrong!!");
        }

    }catch(err){
        console.log(err);
        handleError("Something went wrong!!")
    }
  }

  return (
    <div className="container mt-5">
      <div className="row mt-5">
        <div className="col d-flex justify-content-center mt-5 p-3">
          <Box
            sx={{
              width: 400,
              display: "flex",
              flexDirection: "column",
              gap: 2, // spacing between fields
            }}
          >
            <h3 className="text-center mb-3">Login to LifeConnect</h3>
            <form>
            <TextField
              fullWidth
              label="Email or Username"
              id="emailOrUsername"
              onChnage={handleChange}
            />
            <TextField
              fullWidth
              label="Password"
              id="password"
              type="password"
              onChnage={handleChange}
            />

            <Button
              variant="contained" 
              type="submit"
              sx={{
                backgroundColor: "#cf2b2b",
                "&:hover": { backgroundColor: "#b12222" },
              }}
            >
              Login
            </Button>

            <p className="text-center mt-2">
              Don't have an account?{" "}
              <Link
                to="/signup"
                style={{ color: "#cf2b2b", textDecoration: "none" }}
              >
                Signup
              </Link>
            </p>
            </form>
            <ToastContainer/>
          </Box>
        </div>
      </div>
    </div>
  );
}
