import React from "react";

export default function Hero() {
  return (
    <div className="container">
      <h1 className="text-center ">
        “LifeConnect’s mission is to build a compassionate community where no
        one struggles alone in times of medical need. Through a verified,
        real-time system, we connect donors and recipients seamlessly — ensuring
        that help is always just one click away.”
      </h1>
    </div>
  );
}
