import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Cards() {
  const [donors, setDonors] = useState([]);

useEffect(() => {
  axios
    .get("http://localhost:8080/cards/donar")
    .then((res) => {
      console.log("Server Response:", res.data);   //  Check here
      setDonors(res.data.donors || []);            // Safe fallback
    })
    .catch((err) => console.log("Error fetching donors", err));
}, []);


  return (
    <div className="container p-5">
      <div className="row p-5">

        {donors.map((user, index) => (
          <div key={index} className="card m-3" style={{ width: "18rem" }}>
            <img
              src="/logo.png"
              className="card-img-top border-bottom"
              alt="donor"
            />

            <div className="card-body">
              <h5 className="card-title">{user.name}</h5>

              <div className="d-flex justify-content-between mb-1">
                <p className="card-text m-0">Age : {user.age}</p>
                <p className="card-text m-0">City : {user.city}</p>
              </div>

              <p className="card-text">Blood Group : {user.blood_group}</p>
              <p className="card-text">Email : {user.email}</p>
            </div>
          </div>
        ))}

      </div>
    </div>
  );
}
